var QuizUI={displayNext:function(){myQuiz.hasEnded()?this.displayScore():(this.displayQuestions(),this.displayChoices(),this.displayProgress())},populateIdWithHtml:function(i,t){var e=document.getElementById(i);e.innerHTML=t},displayQuestions:function(){this.populateIdWithHtml("question",myQuiz.getCurrentQuestion().text)},displayChoices:function(){for(var i=myQuiz.getCurrentQuestion().choices,t=0;t<myQuiz.questions.length;t++)this.populateIdWithHtml("choice"+t,i[t]),this.guessHandler("guess"+t,i[t])},displayProgress:function(){var i=myQuiz.currentQuestionIndex+1;this.populateIdWithHtml("progress","Question "+i+" of "+myQuiz.questions.length)},guessHandler:function(i,t){var e=document.getElementById(i);e.onclick=function(){myQuiz.guess(t),QuizUI.displayNext()}},displayScore:function(){var i="<h1>Game Over</h1>";i+="<h2> Your Score is "+myQuiz.score,this.populateIdWithHtml("quiz",i)}};