function Playlist(){this.songs=[],this.currentSongIndex=0}Playlist.prototype.add=function(t){this.songs.push(t)},Playlist.prototype.play=function(){var t=this.songs[this.currentSongIndex];t.play()},Playlist.prototype.stop=function(){var t=this.songs[this.currentSongIndex];t.stop()},Playlist.prototype.next=function(){this.stop(),this.currentSongIndex++,this.currentSongIndex===this.songs.length&&(this.currentSongIndex=0),this.play()},Playlist.prototype.renderInElement=function(){for(var t="",n=0;n<this.songs.length;n++)t+=this.songs[n].toHTML();return t};